provide the scm version update the below code  const vscode = require("vscode"); 
const fetch = require("node-fetch");
const { exec } = require("child_process");

function getGitDiff() {
  return new Promise((resolve, reject) => {
    exec("git diff --cached", { cwd: vscode.workspace.rootPath }, (err, stdout) => {
      if (err) reject(err);
      else resolve(stdout);
    });
  });
}

async function generateCommitMessage(diff) {
  const config = vscode.workspace.getConfiguration("commitMsgGen");
  const apiKey = config.get("apiKey");
  const baseUrl = config.get("baseUrl");
  const model = config.get("model");
  const maxTokens = config.get("maxTokens");

  if (!apiKey) {
    vscode.window.showErrorMessage("API key not set in settings.");
    return null;
  }

  const response = await fetch(${baseUrl}/chat/completions, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "Authorization": Bearer ${apiKey}
    },
    body: JSON.stringify({
      model: model,
      messages: [
        { role: "system", content: "You are an AI that writes clear, concise Git commit messages." },
        { role: "user", content: Generate a short commit message for the following git diff:\n\n${diff} }
      ],
      max_tokens: maxTokens
    })
  });

  if (!response.ok) {
    throw new Error(API Error: ${response.statusText});
  }

  const data = await response.json();
  return data.choices?.[0]?.message?.content.trim();
}

async function insertCommitMessage(message) {
  vscode.scm.inputBox.value = message;
}

function activate(context) {
    // Create a source control instance for this extension
    const mySCM = vscode.scm.createSourceControl('myExtensionSCM', 'My SCM');

    // Command to generate commit message
    let disposable = vscode.commands.registerCommand('extension.generateCommitMessage', function () {
        const commitMessage = 'This is an auto-generated commit message! ';
        
        // Set commit message in our SCM input box
        mySCM.inputBox.value = commitMessage;

        vscode.window.showInformationMessage('Commit message generated!');
    });

    context.subscriptions.push(disposable, mySCM);
}

function deactivate() { }

module.exports = { activate, deactivate };

